#include <windows.h>\n#include <gdiplus.h>\n\nusing namespace Gdiplus;\n\nvoid BlendAlphaImages(Bitmap* srcImage, Bitmap* alphaMaskImage)\n{\n    // Create a new bitmap with the same size as the source image\n    Bitmap blendedImage(srcImage->GetWidth(), srcImage->GetHeight());\n\n    // Create an ImageAttributes object\n    ImageAttributes attribs;\n\n    // Set the alpha mask\n    attribs.SetAlphaMask(alphaMaskImage);\n\n    // Draw the source image onto the new bitmap using the ImageAttributes\n    Graphics graphics(&blendedImage);\n    graphics.DrawImage(srcImage, 0, 0, attribs);\n\n    // blendedImage now contains the blended alpha channels\n}\n\nint main()\n{\n    // Initialize GDI+\n    GdiplusStartupInput gdiplusStartupInput;\n    ULONG_PTR           gdiplusToken;\n    GdiplusStartup(&gdiplusToken, &gdiplusStartupInput, NULL);\n\n    // Load your images\n    Bitmap srcImage(L"source_image.png");\n    Bitmap alphaMaskImage(L"alpha_mask_image.png");\n\n    // Blend the alpha channels\n    BlendAlphaImages(&srcImage, &alphaMaskImage);\n\n    // Shutdown GDI+\n    GdiplusShutdown(gdiplusToken);\n    return 0;\n}