#include <fstream>\n#include <string>\n\nint main() {\n    std::ifstream file("example.txt");\n    if (!file) {\n        // handle error\n    }\n\n    int startLine = 10; // line X\n    int endLine = 20;   // line Y\n    int estimatedLineLength = 50; // adjust this value\n\n    file.seekg(startLine * estimatedLineLength);\n    std::string line;\n    int currentLine = startLine;\n    while (currentLine <= endLine) {\n        std::getline(file, line);\n        // process the line\n        std::cout << line << std::endl;\n        currentLine++;\n    }\n\n    file.close();\n    return 0;\n}