// A simple example of a Proxy class in C++\n#include <iostream>\n\nclass RealObject {\npublic:\n    void doSomething() {\n        std::cout << "Real object is doing something." << std::endl;\n    }\n};\n\nclass ProxyObject {\nprivate:\n    RealObject* realObject;\npublic:\n    ProxyObject() : realObject(nullptr) {}\n\n    ~ProxyObject() {\n        delete realObject;\n    }\n\n    void doSomething() {\n        if (!realObject) {\n            realObject = new RealObject();\n        }\n        realObject->doSomething();\n    }\n};\n\nint main() {\n    ProxyObject proxy;\n    proxy.doSomething();\n    return 0;\n}