#include <string>\n#include <algorithm>\n\nstd::string encryptString(const std::string& str) {\n    std::string encryptedStr;\n    for (char c : str) {\n        encryptedStr += c ^ 0x12; // Simple XOR encryption\n    }\n    return encryptedStr;\n}\n\nint main() {\n    std::string hiddenString1 = "4537774B-CC80-4eda-B3E4-7A9EE77991F5";\n    std::string encryptedString1 = encryptString(hiddenString1);\n    // Use encryptedString1 instead of hiddenString1\n    return 0;\n}