#include <numeric>\n#include <map>\n#include <string>\n\nstruct SMyStruct {\n    int MULT;\n    int VAL;\n};\n\nstd::map<std::string, SMyStruct*> _idToMyStructMap;\n\nint calculateTotal() {\n    return std::accumulate(_idToMyStructMap.begin(), _idToMyStructMap.end(), 0,\n        [](int sum, const auto& pair) {\n            return sum + pair.second->MULT * pair.second->VAL;\n        });\n}