#include <iostream>\nusing namespace std;\n\nvoid findSubsets(int arr[], int n) {\n    int total = 1 << n; // 1 left shift n places is equivalent to 2^n\n\n    for (int i = 0; i < total; i++) {\n        cout << "{";\n        for (int j = 0; j < n; j++) {\n            if (i & (1 << j)) // If the jth bit in the i is set\n                cout << arr[j] << " ";\n        }\n        cout << "}" << endl;\n    }\n}\n\nint main() {\n    int arr[] = {1, 2, 3, 4, 5};\n    int n = sizeof(arr) / sizeof(arr[0]);\n\n    findSubsets(arr, n);\n\n    return 0;\n}