#include <fstream>\n#include <iostream>\n#include <locale>\n#include <string>\n\nint main() {\n    // Set locale for wide character input/output\n    std::locale::global(std::locale("en_US.UTF-16LE")); // UTF-16 little-endian locale\n    \n    std::wifstream file("C:\\appLog.txt");\n    if (!file) {\n        std::cerr << "Failed to open the file." << std::endl;\n        return 1;\n    }\n\n    std::wstring line;\n    std::wstring filterString = L"filter_me"; // String to filter out\n\n    while (std::getline(file, line)) {\n        if (line.find(filterString) == std::wstring::npos) {\n            std::wcout << line << std::endl;\n        }\n    }\n\n    file.close();\n\n    return 0;\n}