#include <Windows.h>\n#include <setupapi.h>\n\n// ...\n\nGUID guid = { 0x... }; // Replace with the GUID of the device class\n\n// Get the device information set for the class\nHDEVINFO hDevInfo = SetupDiGetClassDevs(&guid, NULL, NULL, DIGCF_PRESENT | DIGCF_DEVICEINTERFACE);\n\nif (hDevInfo != INVALID_HANDLE_VALUE)\n{\n    // Enumerate devices in the class\n    for (int i = 0; ; i++)\n    {\n        SP_DEVINFO_DATA devInfo;\n        devInfo.cbSize = sizeof(devInfo);\n\n        if (!SetupDiEnumDeviceInfo(hDevInfo, i, &devInfo))\n            break;\n\n        // Get the device instance ID\n        char instanceId[MAX_DEVICE_ID_LEN];\n        if (SetupDiGetDeviceInstanceId(hDevInfo, &devInfo, instanceId, MAX_DEVICE_ID_LEN, NULL))\n        {\n            // Compare the instance ID to the symbolic name\n            if (lstrcmp(instanceId, symbolicName) == 0)\n            {\n                // You've found the device! Get further information using devInfo.\n                // ...\n                break;\n            }\n        }\n    }\n\n    SetupDiDestroyDeviceInfoList(hDevInfo);\n}