#include <windows.h>\n#include <wincrypt.h>\n\nint main() {\n    // Initialize CSP\n    HCRYPTPROV hCryptProv;\n    CryptAcquireContext(&hCryptProv, NULL, MS_ENH_RSA_PROV, PROV_RSA_FULL, 0);\n\n    // Load RSA public key\n    HCRYPTKEY hPublicKey;\n    CryptImportKey(hCryptProv, publicKeyBytes, publicKeyLen, 0, 0, &hPublicKey);\n\n    // Set OAEP padding\n    DWORD dwFlags = CRYPT_NOHASHOID | CRYPT_OAEP;\n    CryptSetKeyParam(hPublicKey, KP_PADDING, (LPBYTE)&dwFlags, 0);\n\n    // Encrypt data\n    DWORD dwDataLen = 16;\n    BYTE* pData = new BYTE[dwDataLen];\n    // ... fill pData with your data ...\n    DWORD dwEncLen = 0;\n    CryptEncrypt(hPublicKey, 0, TRUE, 0, NULL, &dwEncLen, dwDataLen);\n    BYTE* pEncData = new BYTE[dwEncLen];\n    CryptEncrypt(hPublicKey, 0, TRUE, 0, pData, &dwEncLen, dwDataLen);\n\n    // Clean up\n    delete[] pData;\n    delete[] pEncData;\n    CryptDestroyKey(hPublicKey);\n    CryptReleaseContext(hCryptProv, 0);\n    return 0;\n}