#include <fstream>\n\nint main() {\n    // Create the file\n    std::ofstream file("tmp.txt");\n    if (!file.is_open()) {\n        std::cerr << "Error creating file." << std::endl;\n        return 1;\n    }\n\n    // Write to the file\n    file << "Hello, world!";\n    file.close();\n\n    // Delete the file\n    if (remove("tmp.txt") != 0) {\n        std::cerr << "Error deleting file." << std::endl;\n        return 1;\n    }\n\n    return 0;\n}