#include <cstdio>\n#include <iostream>\n\nint main() {\n    char filename[L_tmpnam];\n    tmpnam(filename);\n    std::cout << "Temporary filename: " << filename << std::endl;\n\n    // Create the temporary file\n    FILE* tempFile = fopen(filename, "w");\n    if (tempFile == NULL) {\n        std::cerr << "Error creating temporary file." << std::endl;\n        return 1;\n    }\n\n    // Write to the temporary file\n    const char* text = "Hello, world!";\n    fwrite(text, sizeof(char), strlen(text), tempFile);\n    fclose(tempFile);\n\n    // Delete the temporary file\n    if (remove(filename) != 0) {\n        std::cerr << "Error deleting temporary file." << std::endl;\n        return 1;\n    }\n\n    return 0;\n}