#include <Windows.h>\n\nint main() {\n    const char* filePath = "C:\\Windows\\System32\\msvcrt80.dll";\n    DWORD fileVersion = 0;\n    DWORD fileSize = GetFileVersionInfoSizeA(filePath, NULL);\n    if (fileSize > 0) {\n        BYTE* fileVersionInfo = new BYTE[fileSize];\n        if (GetFileVersionInfoA(filePath, 0, fileSize, fileVersionInfo)) {\n            VS_FIXEDFILEINFO* fixedFileInfo;\n            UINT fixedFileInfoSize = sizeof(VS_FIXEDFILEINFO);\n            if (VerQueryValueA(fileVersionInfo, "\\", (LPVOID*)&fixedFileInfo, &fixedFileInfoSize)) {\n                fileVersion = fixedFileInfo->dwFileVersionMS;\n                delete[] fileVersionInfo;\n                return fileVersion >= 0x8000000; // 8.0.0.0\n            }\n        }\n        delete[] fileVersionInfo;\n    }\n    return 0;\n}