#include <windows.h>\n#include <oleauto.h>\n\nint main() {\n    // Initialize COM\n    CoInitialize(NULL);\n\n    // Load the type library\n    ITypeLib* pTypeLib = NULL;\n    HRESULT hr = LoadTypeLibEx("your_com_object.tlb", REGKIND_NONE, &pTypeLib);\n    if (FAILED(hr)) {\n        // Error handling\n    }\n\n    // Get the type info for the coclass\n    ITypeInfo* pTypeInfo = NULL;\n    hr = pTypeLib->GetTypeInfoOfGuid(IID_YourCoClass, &pTypeInfo);\n    if (FAILED(hr)) {\n        // Error handling\n    }\n\n    // Get the version information\n    TYPEATTR* pTypeAttr = NULL;\n    hr = pTypeInfo->GetTypeAttr(&pTypeAttr);\n    if (FAILED(hr)) {\n        // Error handling\n    }\n\n    // Print the version information\n    printf("Version: %d.%d\n", pTypeAttr->wMajorVerNum, pTypeAttr->wMinorVerNum);\n\n    // Clean up\n    pTypeInfo->Release();\n    pTypeLib->Release();\n    CoUninitialize();\n\n    return 0;\n}