#include <ctime>\n\nint getWeekNumber(int year, int month, int day) {\n  std::tm time_in = {0};\n  time_in.tm_year = year - 1900; // years since 1900\n  time_in.tm_mon = month - 1;    // months since January\n  time_in.tm_mday = day;\n\n  std::time_t time_temp = std::mktime(&time_in);\n  std::tm* time_out = std::localtime(&time_temp);\n\n  int week_num = (time_out->tm_yday + 6) / 7; // tm_yday is day of year\n\n  return week_num;\n}