#include <iostream>\n#include <string>\n#include <stdexcept>\n\nint main() {\n    int a;\n    std::string input;\n\n    while (true) {\n        std::cout << "Enter a number: ";\n        std::cin >> input;\n\n        try {\n            a = std::stoi(input);\n            break; // input is valid, exit loop\n        } catch (const std::invalid_argument& e) {\n            std::cerr << "Invalid input. Please enter a valid number." << std::endl;\n        } catch (const std::out_of_range& e) {\n            std::cerr << "Input is out of range. Please enter a valid number." << std::endl;\n        }\n    }\n\n    return 0;\n}