#include <xercesc/dom/DOM.hpp>\n#include <xercesc/parsers/XercesDOMParser.hpp>\n#include <xercesc/util/XMLString.hpp>\n#include <xercesc/xpath/XPathEvaluator.hpp>\n\nint main() {\n  XercesDOMParser parser;\n  parser.loadGrammar("example.xml", Grammar::SchemaGrammarType);\n  parser.parse("example.xml");\n\n  DOMDocument* doc = parser.getDocument();\n  DOMNode* root = doc->getDocumentElement();\n\n  XPathEvaluator* evaluator = new XPathEvaluator();\n  XMLCh* xpathExpr = XMLString::transcode("example/xpath/expression");\n  XPathNSResolver* nsResolver = new XPathNSResolver(doc);\n\n  XPathResult* result = evaluator->evaluate(xpathExpr, root, nsResolver);\n\n  // Process the result\n  delete result;\n  delete nsResolver;\n  delete evaluator;\n  return 0;\n}