// my_dll.h\nclass MyClass {\npublic:\n    int add(int a, int b);\n};\n\n// my_dll.cpp\nint MyClass::add(int a, int b) {\n    return a + b;\n}\n\n// my_dll_test.cpp (within the DLL project)\n#include "gtest/gtest.h"\n#include "my_dll.h"\n\nTEST(MyClassTest, AddTest) {\n    MyClass obj;\n    EXPECT_EQ(3, obj.add(1, 2));\n}\n\n// my_dll_test_project.cpp (separate test project)\n#include "gtest/gtest.h"\n#include "my_dll.h"\n\nTEST(MyClassTest, PublishedInterfaceTest) {\n    MyClass obj;\n    EXPECT_EQ(3, obj.add(1, 2));\n}