#include <sstream>\n#include <string>\n\nclass InMemoryIniFile {\npublic:\n    void addSection(const std::string& sectionName) {\n        iniFile << "[" << sectionName << "]\n";\n    }\n\n    void addKeyValue(const std::string& sectionName, const std::string& key, const std::string& value) {\n        iniFile << key << "=" << value << "\n";\n    }\n\n    std::string getIniFileContents() const {\n        return iniFile.str();\n    }\n\nprivate:\n    std::stringstream iniFile;\n};