// Check if the CRT is initialized properly\n// before making any heap allocations\nif (!_crtAssertBusy && !_crtBreakAlloc && !_crtDumpAlloc &&\n    !_crtAssertBusy && !_crtBreakAlloc && !_crtDumpAlloc)\n{\n    // Initialize the CRT\n    _CRT_SECURE_NO_WARNINGS;\n    _crtBreakAlloc = -1;\n    _crtAssertBusy = 0;\n    _crtBreakAlloc = 0;\n    _crtDumpAlloc = 0;\n}\n\n// Also, ensure that you're linking against the correct\n// version of the CRT (C Runtime Library) in your Release build\n// (Multi-threaded DLL [/MD] or Multi-threaded [/MT])