// C++ wrapper DLL (MyCSharpWrapper.cpp)\n#include <Windows.h>\n#include <objbase.h>\n\n// Import the C# DLL as a COM component\n#import "MyCSharpDll.tlb" no_namespace\n\n// Define the native interface\nextern "C" __declspec(dllexport) void MyCSharpMethodWrapper()\n{\n    // Create an instance of the C# class\n    MyCSharpClass* pMyCSharpClass = NULL;\n    HRESULT hr = CoCreateInstance(__uuidof(MyCSharpClass), NULL, CLSCTX_INPROC_SERVER, IID_PPV_ARGS(&pMyCSharpClass));\n\n    if (SUCCEEDED(hr))\n    {\n        // Call the C# method\n        pMyCSharpClass->MyCSharpMethod();\n        pMyCSharpClass->Release();\n    }\n}