#include <boost/asio.hpp>\n#include <boost/asio/ssl.hpp>\n\nint main() {\n    boost::asio::io_service io_service;\n    boost::asio::ssl::context ctx(boost::asio::ssl::context::sslv23);\n    ctx.set_verify_mode(boost::asio::ssl::context::verify_peer);\n    ctx.load_verify_file("path/to/ca/cert");\n\n    boost::asio::ssl::stream<boost::asio::ip::tcp::socket> socket(io_service, ctx);\n    socket.connect(boost::asio::ip::tcp::endpoint(boost::asio::ip::address::from_string("example.com"), 443));\n    // Perform your HTTPS request\n    return 0;\n}