#include <cgicc/Cgicc.h>\n#include <soci.h>\n\nint main() {\n    cgicc::Cgicc cgi;\n\n    // Connect to MySQL database using soci\n    soci::session sql(soci::mysql, "db=mysql_database user=mysql_user password=mysql_password");\n\n    // Execute a query\n    soci::rowset<soci::row> rs = (sql.prepare << "SELECT * FROM my_table");\n\n    // Print the results\n    for (auto row : rs) {\n        std::cout << row["column1"] << std::endl;\n    }\n\n    return 0;\n}