// Load the .NET 1.1 runtime\nICLRMetaHost* pMetaHost = NULL;\nHRESULT hr = CLRCreateInstance(CLSID_CLRMetaHost, IID_PPV_ARGS(&pMetaHost));\nif (SUCCEEDED(hr))\n{\n    ICLRRuntimeHost* pRuntimeHost = NULL;\n    hr = pMetaHost->GetRuntime(L"v1.1.4322", IID_PPV_ARGS(&pRuntimeHost));\n    if (SUCCEEDED(hr))\n    {\n        // Load the .NET 1.1 assembly\n        pRuntimeHost->ExecuteInDefaultAppDomain(L"MyNet11Assembly");\n    }\n}