// example.cpp\n#include "output.h"\n\nint main()\n{\n    IMyInterface* pInterface = nullptr;\n    HRESULT hr = CoCreateInstance(CLSID_MyClass, nullptr, CLSCTX_INPROC_SERVER, IID_IMyInterface, (LPVOID*)&pInterface);\n    if (SUCCEEDED(hr))\n    {\n        BSTR param = SysAllocString(L"Hello, World!");\n        hr = pInterface->MyMethod(param);\n        SysFreeString(param);\n        pInterface->Release();\n    }\n    return 0;\n}