// General-purpose loop (auto-vectorization)\nvoid compute_data(float* data, int size) {\n    for (int i = 0; i < size; i++) {\n        data[i] = some_computation(data[i]);\n    }\n}\n\n// Performance-critical loop (hand-vectorization using OpenCL)\nvoid compute_critical_data(float* data, int size) {\n    // Specify vectorized instructions using OpenCL\n    #pragma OPENCL EXTENSION cl_khr_fp16 : enable\n    __m128 vec_data = _mm_loadu_ps(data);\n    // Perform computations using vectorized instructions\n    vec_data = _mm_fmadd_ps(vec_data, some_computation(vec_data));\n    _mm_storeu_ps(data, vec_data);\n}