#include <Windows.h>\n\nint main() {\n    HKEY hKey;\n    LONG lRes = RegCreateKeyEx(HKEY_CLASSES_ROOT, "CLSID\\{your-clsid}", 0, NULL, REG_OPTION_NON_VOLATILE, KEY_WRITE, NULL, &hKey, NULL);\n    if (lRes == ERROR_SUCCESS) {\n        // Set the COM library registration values\n        lRes = RegSetValueEx(hKey, "InprocServer32", 0, REG_SZ, (LPBYTE)"yourcomlibrary.dll", 14);\n        if (lRes == ERROR_SUCCESS) {\n            // Registration successful\n        } else {\n            // Registration failed\n        }\n        RegCloseKey(hKey);\n    }\n    return 0;\n}