#include <Windows.h>\n\nHANDLE hDir = CreateFileA("C:\\Path\\To\\Monitor", GENERIC_READ, FILE_SHARE_READ | FILE_SHARE_WRITE | FILE_SHARE_DELETE, NULL, OPEN_EXISTING, FILE_FLAG_BACKUP_SEMANTICS, NULL);\n\nif (hDir != INVALID_HANDLE_VALUE)\n{\n    FILE_NOTIFY_INFORMATION buffer[1024];\n    DWORD bytesReturned;\n    while (ReadDirectoryChangesW(hDir, buffer, sizeof(buffer), TRUE, FILE_NOTIFY_CHANGE_FILE_NAME | FILE_NOTIFY_CHANGE_LAST_WRITE, &bytesReturned, NULL, NULL))\n    {\n        // Process changes\n        for (int i = 0; i < bytesReturned / sizeof(FILE_NOTIFY_INFORMATION); i++)\n        {\n            switch (buffer[i].Action)\n            {\n            case FILE_ACTION_ADDED:\n                // File added\n                break;\n            case FILE_ACTION_MODIFIED:\n                // File modified\n                break;\n            }\n        }\n    }\n    CloseHandle(hDir);\n}