#include <windows.h>\n\nvoid RegDeleteTree(HKEY hKey, LPCWSTR lpszKey) {\n    HKEY hSubKey;\n    WCHAR szSubKey[MAX_PATH];\n\n    // Try to open the key\n    if (RegOpenKeyEx(hKey, lpszKey, 0, KEY_READ, &hSubKey) == ERROR_SUCCESS) {\n        DWORD dwSize = MAX_PATH;\n        DWORD dwIndex = 0;\n\n        // Enumerate all subkeys and recursively delete them\n        while (RegEnumKeyEx(hSubKey, dwIndex, szSubKey, &dwSize, NULL, NULL, NULL, NULL) == ERROR_SUCCESS) {\n            RegDeleteTree(hSubKey, szSubKey);\n            dwSize = MAX_PATH;\n            dwIndex++;\n        }\n\n        // Close the key\n        RegCloseKey(hSubKey);\n\n        // Delete the key\n        RegDeleteKey(hKey, lpszKey);\n    }\n}