#include <Windows.h>\n#include <stdio.h>\n\nint main() {\n    HANDLE hPipeRead, hPipeWrite;\n    SECURITY_ATTRIBUTES saAttr = {0};\n    saAttr.nLength = sizeof(SECURITY_ATTRIBUTES);\n    saAttr.bInheritHandle = TRUE;\n    saAttr.lpSecurityDescriptor = NULL;\n\n    // Create a pipe for the stdout redirection\n    if (!CreatePipe(&hPipeRead, &hPipeWrite, &saAttr, 0)) {\n        // Handle error\n    }\n\n    STARTUPINFO si = {0};\n    si.cb = sizeof(STARTUPINFO);\n    si.dwFlags = STARTF_USESTDHANDLES;\n    si.hStdOutput = hPipeWrite;\n    si.hStdError = hPipeWrite;\n\n    PROCESS_INFORMATION pi = {0};\n\n    // Execute the program\n    if (!CreateProcess(NULL, "path/to/your/program.exe", NULL, NULL, TRUE, 0, NULL, NULL, &si, &pi)) {\n        // Handle error\n    }\n\n    CloseHandle(hPipeWrite);\n\n    // Read from the pipe and capture the output\n    char buffer[1024];\n    DWORD bytesRead = 0;\n    while (ReadFile(hPipeRead, buffer, 1024, &bytesRead, NULL)) {\n        // Process the captured output\n        printf("%s", buffer);\n    }\n\n    CloseHandle(hPipeRead);\n    CloseHandle(pi.hProcess);\n    CloseHandle(pi.hThread);\n\n    return 0;\n}