// bbb.dll\nHANDLE hSharedMem = CreateFileMapping(INVALID_HANDLE_VALUE, NULL, PAGE_READWRITE, 0, 1024, "MySharedMem");\nHANDLE hEvent = CreateEvent(NULL, TRUE, FALSE, "MyEvent");\n\n// Set the state and signal the event\nvoid SetState(BOOL state) {\n    // Update the shared memory area\n    LPVOID pSharedMem = MapViewOfFile(hSharedMem, FILE_MAP_ALL_ACCESS, 0, 0, 0);\n    *(BOOL*)pSharedMem = state;\n    UnmapViewOfFile(pSharedMem);\n\n    // Signal the event\n    SetEvent(hEvent);\n}\n\n// ccc.dll\nHANDLE hSharedMem = OpenFileMapping(FILE_MAP_ALL_ACCESS, FALSE, "MySharedMem");\nHANDLE hEvent = OpenEvent(EVENT_ALL_ACCESS, FALSE, "MyEvent");\n\n// Check the event and retrieve the state\nBOOL GetState() {\n    // Check if the event is signaled\n    if (WaitForSingleObject(hEvent, 0) == WAIT_OBJECT_0) {\n        // Read the shared memory area\n        LPVOID pSharedMem = MapViewOfFile(hSharedMem, FILE_MAP_READ, 0, 0, 0);\n        BOOL state = *(BOOL*)pSharedMem;\n        UnmapViewOfFile(pSharedMem);\n        return state;\n    }\n    return FALSE;\n}