#include <windows.h>\n#include <ole2.h>\n\n// Import the application's type library\n#import "Application.tlb"\n\nint main()\n{\n    // Initialize COM\n    CoInitialize(NULL);\n\n    // Create an instance of the application's COM interface\n    IUnknown* pUnk;\n    HRESULT hr = CoCreateInstance(CLSID_Application, NULL, CLSCTX_INPROC_SERVER, IID_IUnknown, (void**)&pUnk);\n\n    if (SUCCEEDED(hr))\n    {\n        // Query the interface for the desired method\n        IApplication* pApp;\n        hr = pUnk->QueryInterface(IID_IApplication, (void**)&pApp);\n\n        if (SUCCEEDED(hr))\n        {\n            // Call a method on the interface\n            pApp->Method();\n\n            // Release the interface\n            pApp->Release();\n        }\n\n        // Release the IUnknown interface\n        pUnk->Release();\n    }\n\n    // Uninitialize COM\n    CoUninitialize();\n\n    return 0;\n}