#include <taskschd.h>\n\nint main() {\n    ITaskScheduler *pITS;\n    HRESULT hr;\n\n    // Initialize COM.\n    CoInitialize(NULL);\n\n    // Create an instance of the Task Scheduler.\n    hr = CoCreateInstance(CLSID_TaskScheduler,\n                          NULL,\n                          CLSCTX_INPROC_SERVER,\n                          IID_ITaskScheduler,\n                          (void **)&pITS);\n\n    if (SUCCEEDED(hr)) {\n        // Use the Task Scheduler instance...\n        // ...\n\n        // Release the Task Scheduler instance.\n        pITS->Release();\n    }\n\n    // Uninitialize COM.\n    CoUninitialize();\n\n    return 0;\n}