#import "msoutl.olb"\n\n// ...\n\n_ApplicationPtr spApp = NULL;\n_MAPIFolderPtr spContactsFolder = NULL;\n_ContactItemPtr spContactItem = NULL;\n\n// Initialize Outlook\nif (SUCCEEDED(CoCreateInstance(CLSID_Application, NULL, CLSCTX_INPROC_SERVER, IID_Application, (LPVOID*)&spApp)))\n{\n    // Get the Contacts folder\n    if (SUCCEEDED(spApp->GetNamespace(L"MAPI", &spNamespace)))\n    {\n        if (SUCCEEDED(spNamespace->GetDefaultFolder(olFolderContacts, &spContactsFolder)))\n        {\n            // Search for contacts\n            _ItemsPtr spItems = NULL;\n            if (SUCCEEDED(spContactsFolder->get_Items(&spItems)))\n            {\n                spItems->put_Filter(L"[FullName] = 'John Doe'"); // search for contacts with full name 'John Doe'\n                _ContactItemPtr spContactItem = NULL;\n                if (SUCCEEDED(spItems->get_Item(1, &spContactItem))) // get the first matching contact\n                {\n                    // Do something with the contact item\n                }\n            }\n        }\n    }\n}