#include <windows.h>\n#include <wincrypt.h>\n\n// Load the certificate from a file\nHANDLE hFile = CreateFile("path/to/rootca.cer", GENERIC_READ, 0, NULL, OPEN_EXISTING, 0, NULL);\nif (hFile == INVALID_HANDLE_VALUE) {\n    // Handle error\n}\n\nDWORD dwFileSize = GetFileSize(hFile, NULL);\nBYTE* pbCertData = new BYTE[dwFileSize];\nDWORD dwBytesRead = 0;\nReadFile(hFile, pbCertData, dwFileSize, &dwBytesRead, NULL);\nCloseHandle(hFile);\n\n// Import the certificate into the Trusted Root Certification Authorities store\nHCERTSTORE hStore = CertOpenStore(CERT_STORE_PROV_SYSTEM, 0, NULL, CERT_SYSTEM_STORE_LOCAL_MACHINE, L"ROOT");\nif (hStore == NULL) {\n    // Handle error\n}\n\nBOOL bSuccess = CertAddEncodedCertificateToStore(hStore, X509_ASN_ENCODING, pbCertData, dwFileSize, CERT_STORE_ADD_REPLACE_EXISTING, NULL);\nif (!bSuccess) {\n    // Handle error\n}\n\nCertCloseStore(hStore, 0);\ndelete[] pbCertData;