#include <wbemidl.h>\n\nint main() {\n    IWbemServices* pSvc = NULL;\n    IWbemClassObject* pClass = NULL;\n    IWbemClassObject* pInstance = NULL;\n    BSTR bstrClass = NULL;\n\n    // Initialize COM\n    CoInitialize(NULL);\n\n    // Connect to the WMI service\n    CoCreateInstance(CLSID_WbemAdministrativeLocator, NULL, CLSCTX_INPROC_SERVER, IID_IWbemServices, (LPVOID*)&pSvc);\n\n    // Set the WMI namespace\n    bstrClass = SysAllocString(L"ROOT\\CIMV2");\n\n    // Query the Win32_PhysicalMemory class\n    pSvc->CreateClassInstance(bstrClass, 0, NULL, IID_IWbemClassObject, (LPVOID*)&pClass);\n\n    // Enumerate the instances of the class\n    pSvc->CreateInstanceEnum(bstrClass, 0, NULL, IID_IWbemClassObject, (LPVOID*)&pInstance);\n\n    // Iterate over the instances\n    while (pInstance) {\n        // Get the capacity and slot number of the physical memory\n        VARIANT capacity;\n        VARIANT slotNumber;\n\n        pInstance->Get(L"Capacity", 0, &capacity, NULL, NULL);\n        pInstance->Get(L"DeviceLocator", 0, &slotNumber, NULL, NULL);\n\n        printf("RAM Slot %d: %d MB\n", slotNumber.uintVal, capacity.uintVal / (1024 * 1024));\n\n        // Release the instance\n        pInstance->Release();\n        pInstance = NULL;\n\n        // Get the next instance\n        pSvc->NextInstance(pInstance, &pInstance);\n    }\n\n    // Release the resources\n    pSvc->Release();\n    pClass->Release();\n    SysFreeString(bstrClass);\n\n    CoUninitialize();\n    return 0;\n}