#include <OpenMesh/Core/Mesh/PolyMesh_ArrayKernelT.hh>\n\nusing namespace OpenMesh;\n\nclass QuadMeshGenerator {\npublic:\n  void generateMesh(PolyMesh_ArrayKernelT<> &mesh, int width, int height) {\n    // Create vertices\n    for (int i = 0; i < width; ++i) {\n      for (int j = 0; j < height; ++j) {\n        mesh.add_vertex(Vec3f(i, j, 0));\n      }\n    }\n\n    // Create quads\n    for (int i = 0; i < width - 1; ++i) {\n      for (int j = 0; j < height - 1; ++j) {\n        int v0 = i + j * width;\n        int v1 = v0 + 1;\n        int v2 = v0 + width;\n        int v3 = v2 + 1;\n        mesh.add_face(v0, v1, v3, v2);\n      }\n    }\n  }\n};