// Custom DLL that forwards calls to the correct version of VendorLibUtils\nextern "C" {\n    // Forward declaration of the original function\n    int (*OriginalFunction)(int, int);\n\n    // Custom implementation that calls the original function\n    int CustomFunction(int a, int b) {\n        // Load the correct version of VendorLibUtils\n        HMODULE hModule = LoadLibraryEx("VendorLibUtils_Ver1.dll", NULL, 0);\n        if (!hModule) {\n            // Handle error\n        }\n\n        // Get the address of the original function\n        OriginalFunction = (int (*)(int, int))GetProcAddress(hModule, "OriginalFunction");\n\n        // Call the original function\n        return OriginalFunction(a, b);\n    }\n}