// mydll.h\nextern "C" __declspec(dllexport) void GetStringArray(char** arr, int* size);\n\n// mydll.cpp\nvoid GetStringArray(char** arr, int* size) {\n    // Create a sample string array\n    const char* strArr[] = {"Hello", "World", "from", "C++"};\n    int arrSize = sizeof(strArr) / sizeof(strArr[0]);\n\n    // Allocate memory for the output array\n    *size = arrSize;\n    *arr = (char**)CoTaskMemAlloc(arrSize * sizeof(char*));\n    for (int i = 0; i < arrSize; i++) {\n        (*arr)[i] = (char*)CoTaskMemAlloc(strlen(strArr[i]) + 1);\n        strcpy((*arr)[i], strArr[i]);\n    }\n}