#include <Windows.h>\n\nint main() {\n    // Replace with your service name\n    const TCHAR* serviceName = _T("MyService");\n\n    // Replace with the new user account's credentials\n    const TCHAR* username = _T("MyUser");\n    const TCHAR* password = _T("MyPassword");\n\n    SC_HANDLE hSCManager = OpenSCManager(NULL, NULL, SC_MANAGER_ALL_ACCESS);\n    if (hSCManager == NULL) {\n        // Error handling...\n    }\n\n    SC_HANDLE hService = OpenService(hSCManager, serviceName, SERVICE_ALL_ACCESS);\n    if (hService == NULL) {\n        // Error handling...\n    }\n\n    SERVICE_CONFIG config = { 0 };\n    config.dwServiceType = SERVICE_WIN32_OWN_PROCESS;\n    config.lpServiceStartName = username;\n    config.lpPassword = password;\n\n    if (!SetServiceConfig(hService, SERVICE_CONFIG_DESCRIPTION, &config)) {\n        // Error handling...\n    }\n\n    CloseServiceHandle(hService);\n    CloseServiceHandle(hSCManager);\n\n    return 0;\n}