#include <WinSock2.h>\n\nint main() {\n    // Create a socket...\n    SOCKET s = socket(AF_INET, SOCK_STREAM, IPPROTO_TCP);\n\n    // Connect to a server...\n    connect(s, ...);\n\n    // Send some data...\n    send(s, "Hello, world!", 13, 0);\n\n    // Flush the send buffer...\n    DWORD bytesReturned = 0;\n    WSAIoctl(s, SIO_FLUSH, NULL, 0, NULL, 0, &bytesReturned, NULL, NULL);\n\n    // Close the socket...\n    closesocket(s);\n    return 0;\n}