#include <wlanapi.h>\n\nint main() {\n    HANDLE hClient = NULL;\n    DWORD dwError = 0;\n    WLAN_INTERFACE_INFO* pInterfaceInfo = NULL;\n\n    dwError = WlanOpenHandle(WLAN_CLIENT_VERSION_XP_SP2, NULL, &hClient);\n    if (dwError != ERROR_SUCCESS) {\n        wprintf(L"Error opening handle: %u\n", dwError);\n        return 1;\n    }\n\n    dwError = WlanEnumInterfaces(hClient, NULL, &pInterfaceInfo);\n    if (dwError != ERROR_SUCCESS) {\n        wprintf(L"Error enumerating interfaces: %u\n", dwError);\n        return 1;\n    }\n\n    for (int i = 0; pInterfaceInfo[i].InterfaceGuid != NULL; i++) {\n        wprintf(L"SSID: %s\n", pInterfaceInfo[i].strInterfaceDescription);\n    }\n\n    WlanCloseHandle(hClient, NULL);\n    return 0;\n}