#include <Windows.h>\n\nint main() {\n    // Load DLL_A\n    HMODULE hDllA = LoadLibraryA("DLL_A.dll");\n    if (!hDllA) {\n        return 1;\n    }\n\n    // Call a function in DLL_A that loads DLL_B\n    FARPROC pfnLoadDllB = GetProcAddress(hDllA, "LoadDllB");\n    if (pfnLoadDllB) {\n        pfnLoadDllB();\n    }\n\n    // Unload DLL_A\n    FreeLibrary(hDllA);\n\n    // Check if DLL_B is still loaded\n    HMODULE hDllB = GetModuleHandleA("DLL_B.dll");\n    if (hDllB) {\n        printf("DLL_B is still loaded\n");\n    } else {\n        printf("DLL_B has been unloaded\n");\n    }\n\n    return 0;\n}