#include <Windows.h>\n#include <iostream>\n\nint main() {\n    HANDLE hProcess = GetCurrentProcess();\n    DWORD dwHandleCount = 0;\n\n    if (GetProcessHandleCount(hProcess, &dwHandleCount)) {\n        std::cout << "Handle count: " << dwHandleCount << std::endl;\n    } else {\n        std::cout << "Failed to get handle count: " << GetLastError() << std::endl;\n    }\n\n    return 0;\n}