#include <iostream>\n\nvoid openConsole() {\n    #ifdef _WIN32\n        AllocConsole();\n        freopen("CONOUT$", "w", stdout);\n        freopen("CONIN$", "r", stdin);\n        freopen("CONOUT$", "w", stderr);\n    #elif defined(unix) || defined(__unix__) || defined(__unix)\n        // Unix-based systems usually have a console by default\n    #endif\n}\n\nvoid closeConsole() {\n    #ifdef _WIN32\n        FreeConsole();\n    #endif\n}