// client.cpp\n#include <dlfcn.h>\n#include <cstdio>\n\nint main() {\n    void* handle = dlopen("./library.so", RTLD_LAZY);\n    if (handle == NULL) {\n        std::printf("Error loading library\n");\n        return 1;\n    }\n\n    void* (*create)() = (void* (*)())dlsym(handle, "create");\n    if (create == NULL) {\n        std::printf("Error finding create function\n");\n        return 1;\n    }\n\n    void (*destroy)(void*) = (void (*)(void*))dlsym(handle, "destroy");\n    if (destroy == NULL) {\n        std::printf("Error finding destroy function\n");\n        return 1;\n    }\n\n    void* p = create();\n    destroy(p);\n\n    return 0;\n}