// library.cpp\n#include <new>\n#include <cstdio>\n\nvoid* operator new(size_t size) {\n    std::printf("New of library called\n");\n    return std::malloc(size);\n}\n\nvoid operator delete(void* p) {\n    std::printf("Delete of library called\n");\n    std::free(p);\n}\n\nextern "C" {\n    void* create() {\n        return new int;\n    }\n\n    void destroy(void* p) {\n        delete p;\n    }\n}