#include <windows.h>\n#include <ole2.h>\n#include <objbase.h>\n\nclass CMyComServer : public IMyComServer\n{\npublic:\n    // ...\n\n    HRESULT MyMethod()\n    {\n        // ...\n\n        // Set connection shutdown notification\n        IRpcOptions* pRpcOptions = NULL;\n        HRESULT hr = CoGetRpcOptions(IID_IRpcOptions, (LPVOID*)&pRpcOptions);\n        if (SUCCEEDED(hr))\n        {\n            hr = pRpcOptions->SetConnectionShutdownNotification(TRUE);\n            if (SUCCEEDED(hr))\n            {\n                // Connection shutdown notification set successfully\n            }\n            pRpcOptions->Release();\n        }\n\n        // ...\n    }\n\n    // ...\n};