#include <Windows.h>\n\n// Set auto-startup for all users (HKLM)\nvoid SetAutoStartupAllUsers(bool enable) {\n    HKEY hKey;\n    RegOpenKeyEx(HKEY_LOCAL_MACHINE, "SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Run", 0, KEY_SET_VALUE, &hKey);\n    if (enable) {\n        RegSetValueEx(hKey, "MyApp", 0, REG_SZ, (LPBYTE)"C:\\Path\\To\\MyApp.exe", (strlen("C:\\Path\\To\\MyApp.exe") + 1) * sizeof(TCHAR));\n    } else {\n        RegDeleteValue(hKey, "MyApp");\n    }\n    RegCloseKey(hKey);\n}\n\n// Set auto-startup for current user (HKCU)\nvoid SetAutoStartupCurrentUser(bool enable) {\n    HKEY hKey;\n    RegOpenKeyEx(HKEY_CURRENT_USER, "SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Run", 0, KEY_SET_VALUE, &hKey);\n    if (enable) {\n        RegSetValueEx(hKey, "MyApp", 0, REG_SZ, (LPBYTE)"C:\\Path\\To\\MyApp.exe", (strlen("C:\\Path\\To\\MyApp.exe") + 1) * sizeof(TCHAR));\n    } else {\n        RegDeleteValue(hKey, "MyApp");\n    }\n    RegCloseKey(hKey);\n}