#include <windows.h>\n#include <ole2.h>\n#include <objbase.h>\n\nclass CMyComServer : public IMyComServer\n{\npublic:\n    // ...\n\n    HRESULT MyMethod()\n    {\n        // ...\n\n        // Request client shutdown notification\n        IClientSecurity* pClientSecurity = NULL;\n        HRESULT hr = CoGetInterfaceSecurity(IID_IClientSecurity, (LPVOID*)&pClientSecurity);\n        if (SUCCEEDED(hr))\n        {\n            hr = pClientSecurity->RequestClientShutdownNotification();\n            if (SUCCEEDED(hr))\n            {\n                // Client shutdown notification requested successfully\n            }\n            pClientSecurity->Release();\n        }\n\n        // ...\n    }\n\n    // ...\n};