#include <fstream>\n#include <string>\n\nint main() {\n    std::ifstream srcFile("source.txt");\n    std::ofstream tempFile("temp.txt");\n\n    std::string line;\n    while (std::getline(srcFile, line)) {\n        // Compare the new line with existing lines in tempFile\n        std::ifstream tempFileRead("temp.txt");\n        std::string existingLine;\n        bool inserted = false;\n        while (std::getline(tempFileRead, existingLine)) {\n            if (line < existingLine) {\n                // Insert the new line before the existing line\n                tempFile.seekp(tempFile.tellp() - existingLine.length() - 1);\n                tempFile << line << std::endl;\n                inserted = true;\n                break;\n            }\n        }\n        if (!inserted) {\n            // Append the new line to the end of tempFile\n            tempFile << line << std::endl;\n        }\n        tempFileRead.close();\n    }\n\n    // Replace the original destination file with the temporary file\n    std::remove("destination.txt");\n    std::rename("temp.txt", "destination.txt");\n\n    return 0;\n}